CREATE TABLE public.bookings (id uuid DEFAULT public.gen_random_uuid() NOT NULL, trip_id uuid DEFAULT public.gen_random_uuid() NOT NULL, passenger_id uuid DEFAULT public.gen_random_uuid() NOT NULL, seats_booked int2 NOT NULL, payment_status text NOT NULL, booking_status text NOT NULL, PRIMARY KEY (id), FOREIGN KEY (trip_id) REFERENCES public.trips (id) ON UPDATE RESTRICT ON DELETE RESTRICT, FOREIGN KEY (passenger_id) REFERENCES auth.users (id) ON UPDATE RESTRICT ON DELETE RESTRICT);
