CREATE TABLE public.transactions (id uuid DEFAULT public.gen_random_uuid() NOT NULL, payer_id uuid DEFAULT public.gen_random_uuid() NOT NULL, payee_id uuid DEFAULT public.gen_random_uuid() NOT NULL, booking_id uuid DEFAULT public.gen_random_uuid() NOT NULL, amount float4 NOT NULL, transaction_type text NOT NULL, status text NOT NULL, timestamp timestamp NOT NULL, PRIMARY KEY (id), FOREIGN KEY (payer_id) REFERENCES auth.users (id) ON UPDATE CASCADE ON DELETE CASCADE, FOREIGN KEY (payee_id) REFERENCES auth.users (id) ON UPDATE RESTRICT ON DELETE RESTRICT, FOREIGN KEY (booking_id) REFERENCES public.bookings (id) ON UPDATE RESTRICT ON DELETE RESTRICT);
